import React, { useState } from "react";
import { useTranslation } from 'react-i18next';
import '../styles/hoverboxsections.css';
import '../styles/WhyUs.css'

const WhyUS = () => {
    const { t, i18n } = useTranslation();
    const [visible, setVisible] = useState({
        pureMaterials: false,
        logicalApproach: false,
        satyanarayanPooja: false,
    });

    const toggleVisibility = (section) => {
        setVisible(prevState => ({
            ...prevState,
            [section]: !prevState[section],
        }));
    };

    return (
        <section className="featured-services">
            <div className="container">
                <h2>{t('whyChoose')}</h2>
                <h3>{t('chooseDetails')}</h3>
                <div className="services">
                    <div className="service">
                        <h3>{t('pureMaterials.title')}</h3>
                        <button onClick={() => toggleVisibility('pureMaterials')}>
                            {visible.pureMaterials ? "Show Less" : "Click to See More Info"}
                        </button>
                        {visible.pureMaterials && (
                            <p>
                                {t('pureMaterials.details')}
                            </p>
                        )}
                    </div>
                    <div className="service">
                        <h3>{t('logicalApproach.title')}</h3>
                        <button onClick={() => toggleVisibility('logicalApproach')}>
                            {visible.logicalApproach ? "Show Less" : "Click to See More Info"}
                        </button>
                        {visible.logicalApproach && (
                            <p>
                                {t('logicalApproach.details')}
                            </p>
                        )}
                    </div>
                    <div className="service">
                        <h3>{t('satyanarayanPooja.title')}</h3>
                        <button onClick={() => toggleVisibility('satyanarayanPooja')}>
                            {visible.satyanarayanPooja ? "Show Less" : "Click to See More Info"}
                        </button>
                        {visible.satyanarayanPooja && (
                            <p>
                                {t('satyanarayanPooja.details')}
                            </p>
                        )}
                    </div>
                </div>
                <button onClick={() => i18n.changeLanguage(i18n.language === 'en' ? 'hi' : 'en')}>
                    Switch to {i18n.language === 'en' ? 'Hindi' : 'English'}
                </button>
            </div>
        </section>
    );
};

export default WhyUS;
