import React, { useState } from 'react';
import '../styles/Authentication.css';

const Authentication = ({ closeForm }) => {
    const [isLogin, setIsLogin] = useState(true);
    const [formData, setFormData] = useState({
        mobileNumber: '',
        otp: '',
        firstName: '',
        lastName: '',
        userType: 'normal' // Default to 'normal'
    });
    const [isOtpSent, setIsOtpSent] = useState(false);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const handleSendOtp = async () => {
        try {
            await fetch('http://localhost:8000/services/send-otp/', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ mobile_number: formData.mobileNumber }),
            });
            setIsOtpSent(true);
            alert('OTP sent successfully.');
        } catch (error) {
            console.error('Error sending OTP:', error);
            alert('Failed to send OTP.');
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const url = isLogin ? 'http://localhost:8000/services/login/' : 'http://localhost:8000/services/register/';
        try {
            const response = await fetch(url, {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(formData),
            });
            const data = await response.json();
            if (data.success) {
                window.location.href = data.redirect_url; // Redirect based on user type
            } else {
                alert(data.error || data.message);
            }
        } catch (error) {
            console.error('Error:', error);
        }
    };

    return (
        <div className="popup-overlay">
            <div className="popup-content">
                <button className="close" onClick={closeForm}>Ã—</button>
                <h2>{isLogin ? 'Login' : 'Signup'}</h2>
                <form onSubmit={handleSubmit}>
                    {!isLogin && (
                        <>
                            <input
                                type="text"
                                name="firstName"
                                value={formData.firstName}
                                onChange={handleInputChange}
                                placeholder="First Name"
                                required
                            />
                            <input
                                type="text"
                                name="lastName"
                                value={formData.lastName}
                                onChange={handleInputChange}
                                placeholder="Last Name"
                                required
                            />
                            <select
                                name="userType"
                                value={formData.userType}
                                onChange={handleInputChange}
                                required
                            >
                                <option value="normal">Normal User</option>
                                <option value="panditji">Panditji</option>
                            </select>
                        </>
                    )}
                    <input
                        type="tel"
                        name="mobileNumber"
                        value={formData.mobileNumber}
                        onChange={handleInputChange}
                        placeholder="Mobile Number"
                        required
                    />
                    {!isLogin && (
                        <button
                            type="button"
                            onClick={handleSendOtp}
                        >
                            Send OTP
                        </button>
                    )}
                    <input
                        type="text"
                        name="otp"
                        value={formData.otp}
                        onChange={handleInputChange}
                        placeholder="OTP"
                        required={!isLogin}
                    />
                    <button type="submit">{isLogin ? 'Login' : 'Signup'}</button>
                </form>
                <button onClick={() => setIsLogin(!isLogin)}>
                    {isLogin ? 'Need an account? Signup' : 'Already have an account? Login'}
                </button>
            </div>
        </div>
    );
};

export default Authentication;
