import React, { useState } from 'react';
import '../styles/auth.css';

const AuthForm = () => {
    const [mobile, setMobile] = useState('');
    const [isRegistered, setIsRegistered] = useState(false);
    const [otp, setOtp] = useState('');
    const [firstName, setFirstName] = useState('');
    const [lastName, setLastName] = useState('');
    const [isOtpSent, setIsOtpSent] = useState(false);

    const handleMobileSubmit = async (e) => {
        e.preventDefault();
        // Check if the user is registered by calling the backend
        const response = await fetch(`/api/check-user?mobile=${mobile}`);
        const data = await response.json();
        setIsRegistered(data.isRegistered);
        // Send OTP to the mobile number
        await fetch(`/api/send-otp?mobile=${mobile}`);
        setIsOtpSent(true);
    };

    const handleOtpSubmit = async (e) => {
        e.preventDefault();
        // Verify OTP and either log in or sign up the user
        const requestData = {
            mobile,
            otp,
            ...(isRegistered ? {} : { firstName, lastName })
        };
        const response = await fetch(isRegistered ? '/api/login' : '/api/signup', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(requestData),
        });
        const result = await response.json();
        if (result.success) {
            // Handle successful login/signup (e.g., redirect to dashboard)
            console.log(result.action);
        } else {
            // Handle error (e.g., show error message)
            console.error('Error:', result.message);
        }
    };

    return (
        <div className="auth-form">
            {!isOtpSent ? (
                <form onSubmit={handleMobileSubmit}>
                    <label>
                        Mobile Number:
                        <input
                            type="text"
                            value={mobile}
                            onChange={(e) => setMobile(e.target.value)}
                            required
                        />
                    </label>
                    <button type="submit">Submit</button>
                </form>
            ) : (
                <form onSubmit={handleOtpSubmit}>
                    <label>
                        OTP:
                        <input
                            type="text"
                            value={otp}
                            onChange={(e) => setOtp(e.target.value)}
                            required
                        />
                    </label>
                    {!isRegistered && (
                        <>
                            <label>
                                First Name:
                                <input
                                    type="text"
                                    value={firstName}
                                    onChange={(e) => setFirstName(e.target.value)}
                                    required
                                />
                            </label>
                            <label>
                                Last Name:
                                <input
                                    type="text"
                                    value={lastName}
                                    onChange={(e) => setLastName(e.target.value)}
                                    required
                                />
                            </label>
                        </>
                    )}
                    <button type="submit">Verify OTP</button>
                </form>
            )}
        </div>
    );
};

export default AuthForm;
