import React, { useState } from "react";
import '../styles/hoverboxsections.css';

const WhyUS = () => {
    // State to manage the visibility of content for each service
    const [visible, setVisible] = useState({
        pureMaterials: false,
        logicalApproach: false,
        satyanarayanPooja: false,
    });

    // Toggle visibility function
    const toggleVisibility = (section) => {
        setVisible(prevState => ({
            ...prevState,
            [section]: !prevState[section],
        }));
    };

    return (
        <section className="featured-services">
            <div className="container">
                <h2>Why To Choose Our Pooja Services</h2>
                <h3>Choosing the right pooja services is crucial for ensuring that the rituals are performed with utmost sincerity and adherence to traditions. Here’s why you should choose our pooja services:</h3>
                <div className="services">
                    <div className="service">
                        <h3>Pure and Authentic Materials</h3>
                        <button onClick={() => toggleVisibility('pureMaterials')}>
                            {visible.pureMaterials ? "Show Less" : "Click to See More Info"}
                        </button>
                        {visible.pureMaterials && (
                            <p>
                                We understand the importance of purity in rituals. That’s why we use only the highest quality materials for our poojas:
                                <ul>
                                    <li><strong>Idols:</strong> Made from sacred and pure metals.</li>
                                    <li><strong>Flowers and Leaves:</strong> Fresh and sourced from pesticide-free gardens.</li>
                                    <li><strong>Sandalwood and Turmeric:</strong> Authentic and pure, ensuring the best aromatic and antiseptic properties.</li>
                                    <li><strong>Incense and Oil Lamps:</strong> Made from natural ingredients to create a serene and spiritually uplifting environment.</li>
                                </ul>
                            </p>
                        )}
                    </div>
                    <div className="service">
                        <h3>Logical and Scientific Approach</h3>
                        <button onClick={() => toggleVisibility('logicalApproach')}>
                            {visible.logicalApproach ? "Show Less" : "Click to See More Info"}
                        </button>
                        {visible.logicalApproach && (
                            <p>
                                Our pooja services are designed not just to uphold tradition but to align with logical and scientific principles. Here’s how:
                                <ul>
                                    <li><strong>Ancient Scriptures:</strong> Each ritual follows the vidhi (procedure) outlined in ancient Hindu scriptures such as the Vedas and Puranas.</li>
                                    <li><strong>Scientific Basis:</strong> We incorporate scientific principles, recognizing the psychological and physiological benefits of each ritual component:</li>
                                    <ul>
                                        <li><strong>Mantra Chanting:</strong> Creates positive vibrations, improving mental clarity and focus.</li>
                                        <li><strong>Homa (Fire Ritual):</strong> Purifies the environment by eliminating airborne bacteria and enhancing positive energy.</li>
                                        <li><strong>Offering Materials:</strong> Each offering, from water to ghee, has specific health benefits and significance, enhancing the ritual’s effectiveness.</li>
                                    </ul>
                                </ul>
                            </p>
                        )}
                    </div>
                    <div className="service">
                        <h3>Satyanarayan Pooja</h3>
                        <button onClick={() => toggleVisibility('satyanarayanPooja')}>
                            {visible.satyanarayanPooja ? "Show Less" : "Click to See More Info"}
                        </button>
                        {visible.satyanarayanPooja && (
                            <p>
                                Book a Pandit Ji for Satyanarayan Pooja.
                            </p>
                        )}
                    </div>
                </div>
            </div>
        </section>
    );
};

export default WhyUS;
