import React from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Header from './components/Header';
import Footer from './components/Footer';
import FindMyPanditji from './components/FindMyPanditji';
import FindMyMaid from './components/FindMyMaid';
import ProfilePage from './components/ProfilePage'; // Add profile page for users
import PanditjiDashboard from './components/PanditjiDashboard'; // Add dashboard for Panditji
import './styles/header.css';
import './styles/footer.css';
import './styles/panditjistyle.css';
import './styles/maidstyles.css';

const App = () => {
    const getUserType = () => {
        return localStorage.getItem('userType'); // 'normal' or 'panditji'
    };

    const userType = getUserType();

    return (
        <Router>
            <Header />
            <main>
                <Routes>
                    <Route path="/" element={<FindMyPanditji />} />
                    <Route path="/findmypanditji" element={<FindMyPanditji />} />
                    <Route path="/findmymaid" element={<FindMyMaid />} />
                    <Route path="/profile" element={userType === 'panditji' ? <PanditjiDashboard /> : <ProfilePage />} />
                    {/* Redirect to profile based on user type */}
                </Routes>
            </main>
            <Footer />
        </Router>
    );
};

export default App;
