import React, { useState } from 'react';
import axios from 'axios';

const Authentication = ({ closeForm, setIsLoggedIn }) => {
    const [isRegistering, setIsRegistering] = useState(true);
    const [mobileNumber, setMobileNumber] = useState('');
    const [otp, setOtp] = useState('');
    const [firstName, setFirstName] = useState('');
    const [lastName, setLastName] = useState('');
    const [userType, setUserType] = useState('normal');
    const [error, setError] = useState('');
    const [success, setSuccess] = useState('');

    const handleSendOtp = async () => {
        try {
            const response = await axios.post('http://localhost:8000/services/send_otp/', { mobileNumber });
            if (response.data.success) {
                setSuccess(response.data.message);
            } else {
                setError(response.data.message);
            }
        } catch (err) {
            setError('An error occurred.');
        }
    };

    const handleVerifyOtp = async () => {
        try {
            const response = await axios.post('http://localhost:8000/services/verify_otp/', { otp, mobileNumber });
            if (response.data.success) {
                if (isRegistering) {
                    await handleRegisterUser();
                } else {
                    await handleLoginUser();
                }
            } else {
                setError(response.data.message);
            }
        } catch (err) {
            setError('An error occurred.');
        }
    };

    const handleRegisterUser = async () => {
        try {
            const response = await axios.post('http://localhost:8000/services/register_user/', { mobileNumber, otp, firstName, lastName, userType });
            if (response.data.success) {
                localStorage.setItem('user', JSON.stringify({ mobileNumber, isAuthenticated: true }));
                setIsLoggedIn(true);
                closeForm();
            } else {
                setError(response.data.message);
            }
        } catch (err) {
            setError('An error occurred.');
        }
    };

    const handleLoginUser = async () => {
        try {
            const response = await axios.post('http://localhost:8000/services/login_user/', { mobileNumber, otp });
            if (response.data.success) {
                localStorage.setItem('user', JSON.stringify({ mobileNumber, isAuthenticated: true }));
                setIsLoggedIn(true);
                closeForm();
            } else {
                setError(response.data.message);
            }
        } catch (err) {
            setError('An error occurred.');
        }
    };

    return (
        <div className="auth-form">
            <h2>{isRegistering ? 'Register' : 'Login'}</h2>
            {error && <div className="error">{error}</div>}
            {success && <div className="success">{success}</div>}
            <input 
                type="text" 
                placeholder="Mobile Number" 
                value={mobileNumber} 
                onChange={(e) => setMobileNumber(e.target.value)} 
            />
            <input 
                type="text" 
                placeholder="OTP" 
                value={otp} 
                onChange={(e) => setOtp(e.target.value)} 
            />
            {isRegistering && (
                <>
                    <input 
                        type="text" 
                        placeholder="First Name" 
                        value={firstName} 
                        onChange={(e) => setFirstName(e.target.value)} 
                    />
                    <input 
                        type="text" 
                        placeholder="Last Name" 
                        value={lastName} 
                        onChange={(e) => setLastName(e.target.value)} 
                    />
                    <select 
                        value={userType} 
                        onChange={(e) => setUserType(e.target.value)}
                    >
                        <option value="normal">Normal User</option>
                        <option value="panditji">Panditji</option>
                    </select>
                </>
            )}
            <button onClick={handleSendOtp}>Send OTP</button>
            <button onClick={handleVerifyOtp}>
                {isRegistering ? 'Register' : 'Login'}
            </button>
            <button onClick={() => setIsRegistering(!isRegistering)}>
                Switch to {isRegistering ? 'Login' : 'Register'}
            </button>
            <button onClick={closeForm}>Close</button>
        </div>
    );
};

export default Authentication;
