import React, { useState } from 'react';
import axios from 'axios';

const Authentication = ({ closeForm, setIsLoggedIn }) => {
    const [formType, setFormType] = useState('login'); // 'login' or 'signup'
    const [mobileNumber, setMobileNumber] = useState('');
    const [otp, setOtp] = useState('');
    const [firstName, setFirstName] = useState('');
    const [lastName, setLastName] = useState('');
    const [userType, setUserType] = useState('user'); // 'panditji' or 'user'
    const [otpSent, setOtpSent] = useState(false);
    const [message, setMessage] = useState('');

    const handleSendOtp = async () => {
        try {
            await axios.post('http://localhost:8000/services/send_otp/', { mobileNumber });
            setOtpSent(true);
            setMessage('OTP sent successfully.');
        } catch (error) {
            setMessage('Error sending OTP.');
        }
    };

    const handleVerifyOtp = async () => {
        try {
            const response = await axios.post('http://localhost:8000/services/verify_otp/', { mobileNumber, otp });
            print(response)
            if (response.data.success) {
                if (formType === 'signup') {
                    await handleRegister();
                } else {
                    localStorage.setItem('user', JSON.stringify({ isAuthenticated: true, userType }));
                    setIsLoggedIn(true);
                    closeForm();
                }
            } else {
                setMessage('OTP verification failed.');
            }
        } catch (error) {
            setMessage('Error verifying OTP.');
        }
    };

    const handleRegister = async () => {
        try {
            await axios.post('http://localhost:8000/services/register/', {
                mobileNumber,
                firstName,
                lastName,
                userType
            });
            localStorage.setItem('user', JSON.stringify({ isAuthenticated: true, userType }));
            setIsLoggedIn(true);
            closeForm();
        } catch (error) {
            setMessage('Error during registration.');
        }
    };

    return (
        <div className="auth-modal">
            <div className="auth-form">
                <button className="close-btn" onClick={closeForm}>×</button>
                {otpSent ? (
                    <>
                        <h2>{formType === 'signup' ? 'Sign Up' : 'Log In'}</h2>
                        <input
                            type="text"
                            placeholder="Enter OTP"
                            value={otp}
                            onChange={(e) => setOtp(e.target.value)}
                        />
                        <button onClick={handleVerifyOtp}>Verify OTP</button>
                    </>
                ) : (
                    <>
                        <h2>{formType === 'signup' ? 'Sign Up' : 'Log In'}</h2>
                        <input
                            type="text"
                            placeholder="Mobile Number"
                            value={mobileNumber}
                            onChange={(e) => setMobileNumber(e.target.value)}
                        />
                        {formType === 'signup' && (
                            <>
                                <input
                                    type="text"
                                    placeholder="First Name"
                                    value={firstName}
                                    onChange={(e) => setFirstName(e.target.value)}
                                />
                                <input
                                    type="text"
                                    placeholder="Last Name"
                                    value={lastName}
                                    onChange={(e) => setLastName(e.target.value)}
                                />
                                <select value={userType} onChange={(e) => setUserType(e.target.value)}>
                                    <option value="user">User</option>
                                    <option value="panditji">Pandit Ji</option>
                                </select>
                            </>
                        )}
                        <button onClick={handleSendOtp}>Send OTP</button>
                    </>
                )}
                <p>{message}</p>
                <p onClick={() => setFormType(formType === 'signup' ? 'login' : 'signup')}>
                    {formType === 'signup' ? 'Already have an account? Log In' : "Don't have an account? Sign Up"}
                </p>
            </div>
        </div>
    );
};

export default Authentication;
