import React from 'react';
import { BrowserRouter as Router, Route, Switch, Redirect } from 'react-router-dom';
import Header from './components/Header';
import Home from './components/Home';
import About from './components/About';
import Contact from './components/Contact';
import FindMyMaid from './components/FindMyMaid';
import FindMyPanditji from './components/FindMyPanditji';
import './styles/app.css';
import Footer from './components/Footer';
import ProfilePage from './components/ProfilePage'; // Add profile page for users
import PanditjiDashboard from './components/PanditjiDashboard'; // Add dashboard for Panditji
import './styles/header.css';
import './styles/footer.css';
import './styles/panditjistyle.css';
import './styles/maidstyles.css';

const App = () => {
    return (
        <Router>
            <Header />
            <Switch>
                <Route path="/" exact component={Home} />
                <Route path="/about" component={About} />
                <Route path="/contact" component={Contact} />
                <Route path="/findmymaid" component={FindMyMaid} />
                <PrivateRoute path="/findmypanditji" component={FindMyPanditji} />
                <Redirect to="/" />
            </Switch>
        </Router>
    );
};

const PrivateRoute = ({ component: Component, ...rest }) => {
    const isAuthenticated = JSON.parse(localStorage.getItem('user'))?.isAuthenticated;

    return (
        <Route
            {...rest}
            render={props =>
                isAuthenticated ? (
                    <Component {...props} />
                ) : (
                    <Redirect to="/" />
                )
            }
        />
    );
};

export default App;
