import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import '../styles/header.css';
import Authentication from './Authentication'; // Import the combined Auth form component

const Header = () => {
    const [showAuthForm, setShowAuthForm] = useState(false);

    const handleLoginSignupClick = () => {
        setShowAuthForm(true);
    };

    return (
        <header>
            <div className="container">
                <div className="logo">Pandit Ji Service</div>
                <nav>
                    <ul>
                        <li><Link to="/" className="nav-link">Home</Link></li>
                        <li className="nav-dropdown">
                            <a href="#" className="nav-link">Services</a>
                            <ul className="dropdown-menu">
                                <li><Link to="/findmymaid">Maid Service</Link></li>
                                <li><Link to="/findmypanditji">Pandit Ji Service</Link></li>
                            </ul>
                        </li>
                        <li><Link to="/about" className="nav-link">About Us</Link></li>
                        <li><Link to="/contact" className="nav-link">Contact Us</Link></li>
                        <li><a id="loginSignupBtn" className="login-btn" onClick={handleLoginSignupClick}>Login/Signup</a></li>
                    </ul>
                </nav>
            </div>
            {showAuthForm && <Authentication closeForm={() => setShowAuthForm(false)} />}
        </header>
    );
};

export default Header;
