import React, { useState } from 'react';
import 'services\src\styles\Authentication.css'; // Import CSS for styling

const Authentication = () => {
    const [isLogin, setIsLogin] = useState(true);

    const handleToggle = () => setIsLogin(!isLogin);

    const handleSubmit = async (event) => {
        event.preventDefault();
        const formData = new FormData(event.target);

        const response = await fetch(`http://localhost:8000/api/${isLogin ? 'login' : 'signup'}/`, {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({
                mobile_number: formData.get('mobileNumber'),
                first_name: formData.get('firstName'),
                last_name: formData.get('lastName'),
            }),
        });

        const data = await response.json();
        if (data.success) {
            localStorage.setItem('userType', data.userType); // Save user type
            window.location.href = '/profile'; // Redirect based on user type
        } else {
            alert(data.message);
        }
    };

    return (
        <div id="authPopup" className="auth-popup">
            <div className="auth-popup-content">
                <span className="close" onClick={() => document.getElementById('authPopup').style.display = 'none'}>&times;</span>
                <h2>{isLogin ? 'Login' : 'Sign Up'}</h2>
                <form onSubmit={handleSubmit}>
                    {!isLogin && (
                        <>
                            <label htmlFor="firstName">First Name:</label>
                            <input type="text" id="firstName" name="firstName" required />
                            <label htmlFor="lastName">Last Name:</label>
                            <input type="text" id="lastName" name="lastName" required />
                        </>
                    )}
                    <label htmlFor="mobileNumber">Mobile Number:</label>
                    <input type="tel" id="mobileNumber" name="mobileNumber" required />
                    <button type="submit">{isLogin ? 'Login' : 'Sign Up'}</button>
                </form>
                <button onClick={handleToggle}>
                    {isLogin ? 'Switch to Sign Up' : 'Switch to Login'}
                </button>
            </div>
        </div>
    );
};

export default Authentication;
