import React, { useState } from "react";
import { useTranslation } from 'react-i18next';
import '../styles/hoverboxsections.css'; // Ensure this includes the necessary styles

const WhyUS = () => {
    const { t, i18n } = useTranslation();
    const [visible, setVisible] = useState({
        pureMaterials: false,
        logicalApproach: false,
        satyanarayanPooja: false,
    });

    const toggleVisibility = (section) => {
        setVisible(prevState => ({
            ...prevState,
            [section]: !prevState[section],
        }));
    };

    return (
        <section className="featured-services">
            <div className="container">
                <h2>{t('whyChoose')}</h2>
                <h3>{t('chooseDetails')}</h3>
                <div className="services">
                    {['pureMaterials', 'logicalApproach', 'satyanarayanPooja'].map(section => (
                        <div className="service" key={section}>
                            <h3>{t(`${section}.title`)}</h3>
                            <button onClick={() => toggleVisibility(section)}>
                                {visible[section] ? "Show Less" : "Click to See More Info"}
                            </button>
                            {visible[section] && (
                                <ul>
                                    {t(`${section}.details`, { returnObjects: true }).map((item, index) => (
                                        <li key={index}>{item}</li>
                                    ))}
                                </ul>
                            )}
                        </div>
                    ))}
                </div>
                <button onClick={() => i18n.changeLanguage(i18n.language === 'en' ? 'hi' : 'en')}>
                    Switch to {i18n.language === 'en' ? 'Hindi' : 'English'}
                </button>
            </div>
        </section>
    );
};

export default WhyUS;
